import streamlit as st
from template_sidebar import sidebar_menu
import math

st.set_page_config(
    page_title="Keliling Bangun Datar",
    layout="wide"
)

sidebar_menu()

st.title("üìè Keliling Bangun Datar")

# MODE
mode = st.radio(
    "Mode Pembelajaran:",
    ["Belajar", "Latihan"],
    horizontal=True
)


# MENU
menu = st.selectbox(
    "Pilih Bangun Datar:",
    [
        "Persegi",
        "Persegi Panjang",
        "Segitiga",
        "Lingkaran",
        "Jajar Genjang",
        "Trapesium",
        "Belah Ketupat",
        "Layang-layang"
    ]
)

st.divider()


# MODE BELAJAR
if mode == "Belajar":
    col1, col2 = st.columns([2, 1])

    if menu == "Persegi":
        with col1:
            st.subheader("Keliling Persegi")
            st.write("""
            Persegi memiliki empat sisi yang sama panjang.
            Keliling persegi adalah jumlah seluruh sisi-sisinya.
            Karena semua sisi sama, keliling dapat dihitung
            dengan mengalikan panjang sisi dengan 4.
            """)
        with col2:
            st.image(
                "data:image/png;base64,",
                caption="Rumus: K = 4 x s"
            )

    elif menu == "Persegi Panjang":
        with col1:
            st.subheader("Keliling Persegi Panjang")
            st.write("""
            Persegi panjang memiliki dua pasang sisi yang sama panjang.
            Kelilingnya adalah jumlah seluruh sisi, yaitu dua kali
            jumlah panjang dan lebar.
            """)
        with col2:
            st.image(
                "data:image/png;base64,",
                caption="Rumus: K = 2 x (p + l)"
            )

    elif menu == "Segitiga":
        with col1:
            st.subheader("Keliling Segitiga")
            st.write("""
            Segitiga memiliki tiga sisi.
            Keliling segitiga adalah jumlah panjang ketiga sisinya,
            tanpa memperhatikan jenis segitiganya.
            """)
        with col2:
            st.image(
                "data:image/jpeg;base64,",
                caption="Rumus: K = a + b + c"
            )

    elif menu == "Lingkaran":
        with col1:
            st.subheader("Keliling Lingkaran")
            st.write("""
            Keliling lingkaran adalah panjang garis lengkung yang
            membatasi lingkaran. Perhitungannya menggunakan
            konstanta œÄ (pi) dengan nilai sekitar 3,14.
            """)
        with col2:
            st.image(
                "data:image/png;base64,",
                caption="Rumus: K = 2 x œÄ x r"
            )

    elif menu == "Jajar Genjang":
        with col1:
            st.subheader("Keliling Jajar Genjang")
            st.write("""
            Jajar genjang memiliki dua pasang sisi yang sama panjang.
            Kelilingnya dihitung dengan menjumlahkan seluruh sisi,
            atau dua kali jumlah sisi yang berdekatan.
            """)
        with col2:
            st.image(
                "data:image/png;base64,",
                caption="Rumus: K = 2 x (a + b)"
            )

    elif menu == "Trapesium":
        with col1:
            st.subheader("Keliling Trapesium")
            st.write("""
            Trapesium memiliki empat sisi.
            Keliling trapesium adalah jumlah seluruh sisi,
            baik sisi sejajar maupun tidak sejajar.
            """)
        with col2:
            st.image(
                "data:image/jpeg;base64,",
                caption="Rumus: K = a + b + c + d"
            )

    elif menu == "Belah Ketupat":
        with col1:
            st.subheader("Keliling Belah Ketupat")
            st.write("""
            Belah ketupat memiliki empat sisi yang sama panjang.
            Oleh karena itu, kelilingnya adalah empat kali
            panjang satu sisi.
            """)
        with col2:
            st.image(
                "data:image/png;base64,",
                caption="Rumus: K = 4 x s"
            )

    elif menu == "Layang-layang":
        with col1:
            st.subheader("Keliling Layang-layang")
            st.write("""
            Layang-layang memiliki dua pasang sisi yang sama panjang.
            Kelilingnya dihitung dengan menjumlahkan semua sisi
            atau dua kali jumlah sisi yang berbeda panjang.
            """)
        with col2:
            st.image(
                "data:image/png;base64,",
                caption="Rumus: K = 2 x (a + b)"
            )


# MODE LATIHAN
else:
    if menu == "Persegi":
        s = st.number_input("Masukkan sisi", min_value=0.0)
        if st.button("Hitung"):
            st.success(f"Keliling = {4 * s}")

    elif menu == "Persegi Panjang":
        p = st.number_input("Masukkan panjang", min_value=0.0)
        l = st.number_input("Masukkan lebar", min_value=0.0)
        if st.button("Hitung"):
            st.success(f"Keliling = {2 * (p + l)}")

    elif menu == "Segitiga":
        a = st.number_input("Sisi a", min_value=0.0)
        b = st.number_input("Sisi b", min_value=0.0)
        c = st.number_input("Sisi c", min_value=0.0)
        if st.button("Hitung"):
            st.success(f"Keliling = {a + b + c}")

    elif menu == "Lingkaran":
        r = st.number_input("Jari-jari", min_value=0.0)
        if st.button("Hitung"):
            st.success(f"Keliling = {2 * math.pi * r:.2f}")

    elif menu == "Jajar Genjang":
        a = st.number_input("Sisi a", min_value=0.0)
        b = st.number_input("Sisi b", min_value=0.0)
        if st.button("Hitung"):
            st.success(f"Keliling = {2 * (a + b)}")

    elif menu == "Trapesium":
        a = st.number_input("Sisi a", min_value=0.0)
        b = st.number_input("Sisi b", min_value=0.0)
        c = st.number_input("Sisi c", min_value=0.0)
        d = st.number_input("Sisi d", min_value=0.0)
        if st.button("Hitung"):
            st.success(f"Keliling = {a + b + c + d}")

    elif menu == "Belah Ketupat":
        s = st.number_input("Panjang sisi", min_value=0.0)
        if st.button("Hitung"):
            st.success(f"Keliling = {4 * s}")

    elif menu == "Layang-layang":
        a = st.number_input("Sisi a", min_value=0.0)
        b = st.number_input("Sisi b", min_value=0.0)
        if st.button("Hitung"):
            st.success(f"Keliling = {2 * (a + b)}")
